mapper.select:
SELECT count(*) FROM nandal_qna_board where contentid=#{contentid}
AND ${searchKey} LIKE '%'||#{searchWord}||'%'
AND subject LIKE '%'||#{searchWord}||'%' OR content LIKE '%'||#{searchWord}||'%'

select b.*, u.nickname from (select rownum as rm, a.* from ( select * from nandal_qna_board where contentid=#{contentid}
and ${searchKey} LIKE '%'||#{searchWord}||'%'
and subject LIKE '%'||#{searchWord}||'%' OR content LIKE '%'||#{searchWord}||'%'
order by ref desc, re_step asc) a)b left join nandal_user u on b.writer=u.usercode
where b.rm>=#{startRow} and b.rm <=#{endRow}
order by ref desc, re_step asc

mapper.insert:
INSERT INTO nandal_qna_board(contentid, num, writer, subject, reg_date, readcount, ref, re_step, re_level, content, ip, upload) VALUES(#{contentid}, nandal_qna_board_num_seq.nextval, #{writer, jdbcType=VARCHAR}, #{subject}, sysdate, 0,
nandal_qna_board_num_seq.nextval
#{ref}
, #{re_step}, #{re_level}, #{content, jdbcType=VARCHAR}, #{ip, jdbcType=VARCHAR}, #{upload, jdbcType=VARCHAR}
)

mapper.select:
SELECT
upload FROM nandal_qna_board
WHERE num=#{num}

mapper.update:
UPDATE nandal_qna_board
SET subject=#{subject},
content=#{content}

	, upload=#{upload}

WHERE num=#{num}
