mapper.select:
SELECT count(*)
FROM NANDAL_USER
WHERE id=#{id}

SELECT count(*)
FROM NANDAL_USER
WHERE nickname=#{nickname}

mapper.update:
UPDATE NANDAL_USER
SET nickname=#{nickname}, password=#{password}, phone=#{phone},
email=#{email}, loc=#{loc},

	img=#{img}

WHERE id=#{id}

mapper.select:
SELECT img 
FROM NANDAL_USER
WHERE usercode=#{usercode}

mapper.update:
UPDATE NANDAL_USER
SET usergrade=0
WHERE id=#{id} and password=#{password}

mapper.select:
SELECT count(*) FROM nandal_review_board where writer=#{writer}
AND ${searchKey} LIKE '%'||#{searchWord}||'%'
AND subject LIKE '%'||#{searchWord}||'%' OR content LIKE '%'||#{searchWord}||'%'

select b.*, u.nickname from (select rownum as rm, a.* from ( select * from nandal_review_board where writer=#{writer}
and ${searchKey} LIKE '%'||#{searchWord}||'%'
and subject LIKE '%'||#{searchWord}||'%' OR content LIKE '%'||#{searchWord}||'%'
order by num DESC) a)b left join nandal_user u on b.writer=u.usercode
where b.rm>=#{startRow} and b.rm <=#{endRow}
order by num DESC

SELECT count(*) FROM nandal_qna_board where writer=#{writer}
AND ${searchKey} LIKE '%'||#{searchWord}||'%'
AND subject LIKE '%'||#{searchWord}||'%' OR content LIKE '%'||#{searchWord}||'%'

select b.*, u.nickname from (select rownum as rm, a.* from ( select * from nandal_qna_board where writer=#{writer}
and ${searchKey} LIKE '%'||#{searchWord}||'%'
and subject LIKE '%'||#{searchWord}||'%' OR content LIKE '%'||#{searchWord}||'%'
order by num DESC) a)b left join nandal_user u on b.writer=u.usercode
where b.rm>=#{startRow} and b.rm <=#{endRow}
order by num DESC
